import React, { Component } from 'react';
import { connect } from "react-redux";

class Home extends Component {
    constructor(props){
        super(props);
        this.state={
            fname:'',
            lname:'',
            phone:''
        }
    }
    methodLogin(event){ 
        this.setState({
            fname:event.target.value
        }, (previousState) => {
            //console.log(this.state.fname);        
        })            
    }
    methodLoginlname(event){ 
        this.setState({
            lname:event.target.value
        }, (previousState) => {
            //console.log(this.state.fname);        
        })            
    }
    methodLoginphone(event){ 
        this.setState({
            phone:event.target.value
        }, (previousState) => {
            //console.log(this.state.fname);        
        })            
    }
    handleSubmit(event){
        event.preventDefault();
        //console.log(this.state.fname); 
        this.props.dispatch({
            type:'ADD_USER',
            data: [
                {'fname': this.state.fname},
                {'1': this.state.lname},
                {'2': this.state.phone}
              ]
          })
    }
    render() {
        return (
            <div className="col-md-12">
            <div className="row">
            <div className="col-sm-4"></div>
            <div className="col-sm-4 box-full">
      <h4>Create user</h4>
     <div className="input-group">
     <form onSubmit={this.handleSubmit.bind(this)}>
                <input type="text" placeholder="Enter first name" className="form-control"  onChange={this.methodLogin.bind(this)}/>
                <input type="text" placeholder="Enter last name" className="form-control" onChange={this.methodLoginlname.bind(this)}/>
                <input type="text" placeholder="Enter mobile no" className="form-control" onChange={this.methodLoginphone.bind(this)}/>
                <button type="submit" className="btn btn-success">Submit</button>
     </form>
      </div>
    </div>
    <div className="col-sm-4"></div>
    </div>
    </div>
        )
    }
}


export default connect()(Home);
