import _ from "lodash";

const userReducer = (state = [], action) => {
    switch(action.type) {
        case 'ADD_USER':
            return state.concat(action.data);
        case 'REMOVE_USER':
            return state.filter((item, index) => item.id !== action.data);
        // return _.remove(state, 
        //     {
        //         firstname:action.data
        //     })
        case 'EDIT_USER':
        return state.arr.splice(index, 1, {id: index.id, firstname: index.firstname, lastname: index.lastname, phone: index.phone});
        default:
            return state;
    }
}

export default userReducer;
