import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { connect } from "react-redux";


class Users extends Component {
    constructor(props) {
        super(props);
    }

    render() {
        return (
            //<nav>
                //<ul>
                    //<li><Link to='/profile/1'>Profile 1</Link></li>
                    //<li><Link to='/profile/2'>Profile 2</Link></li>
                //</ul>
            //</nav>
            <div className="col-sm-12">
            <table className="table">
                <thead>
                    <th>Sl no.</th>
                    <th>First name</th>
                    <th>View</th>
                </thead>
      {this.props.userlist.map(function(user, i){
       return <tr>
           <td>{i}</td>
           <td>{user}</td>
           <td><Link to={`/profile/${i}`}>View</Link></td>
           <td><button onClick={()=> this.handleRemoveUser(i)}>Remove</button></td>
           </tr>
      }
     )}
      </table>
    </div>
        )
    }
}

const mapStateToProps = (state) => {
  return {
    userlist: state
  }
}

export default connect(mapStateToProps)(Users);
