import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { connect } from "react-redux";

class Delete extends Component {
    constructor(props) {
        super(props);
    }
    handleRemove(event, param){
        this.props.dispatch({
            type:'REMOVE_USER',
            data: event
          })
          //this.props.history.push('/users');
    }

    render() {
        return (
            <div className="col-md-12 pad-side6">
              <h1>{this.props.userlist[this.props.match.params.id]}</h1>
              <h4>Are you sure you want to delete this user?</h4>
              <button type="button" className="btn btn-danger" onClick = { this.handleRemove.bind(this, this.props.match.params.id) }>Delete</button>
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return {
      userlist: state
    }
  }

  export default connect(mapStateToProps)(Delete);
