import React, { Component } from 'react';
import { connect } from "react-redux";

class Home extends Component {
    constructor(props){
        super(props);
        this.state={
            fname:'asd',
            lname:'',
            phone:''
        }
    }
    methodLogin(event){ 
        this.setState({
            fname:event.target.value
        }, (previousState) => {
            //console.log(this.state.fname);        
        })   
        this.props.dispatch({
            type:'ADD_USER',
            //data: this.state.fname+this.state.lname+this.state.phone
            data:{
                id: Math.floor(Math.random()*1000),
                //id: 1,
                fielddata: this.state.fname
            },
          })         
    }
    methodLoginlname(event){ 
        this.setState({
            lname:event.target.value
        }, (previousState) => {
            //console.log(this.state.fname);        
        }) 
        this.props.dispatch({
            type:'ADD_USER',
            //data: this.state.fname+this.state.lname+this.state.phone
            data:{
                id: Math.floor(Math.random()*1000),
                //id: 1,
                fielddata: this.state.lname
            },
          })            
    }
    methodLoginphone(event){ 
        this.setState({
            phone:event.target.value
        }, (previousState) => {
            //console.log(this.state.fname);        
        }) 
        this.props.dispatch({
            type:'ADD_USER',
            //data: this.state.fname+this.state.lname+this.state.phone
            data:{
                id: Math.floor(Math.random()*1000),
                //id: 1,
                fielddata: this.state.phone
            },
          })            
    }
    handleSubmit(event){
        event.preventDefault();
        //console.log(this.state.fname); 

        this.props.history.push('/users');
    }
    render() {
        return (
            <div className="col-md-12">
            <div className="row">
            <div className="col-sm-4"></div>
            <div className="col-sm-4 box-full">
      <h4>Create user</h4>
     <div className="input-group">
     <form onSubmit={this.handleSubmit.bind(this)}>
                <input type="text" value={this.state.fname} placeholder="Enter first name" className="form-control"  onChange={this.methodLogin.bind(this)}/>
                <input type="text" placeholder="Enter last name" className="form-control" onChange={this.methodLoginlname.bind(this)}/>
                <input type="text" placeholder="Enter phone" className="form-control" onChange={this.methodLoginphone.bind(this)}/>
                <button type="submit" className="btn btn-success">Submit</button>
     </form>
      </div>
    </div>
    <div className="col-sm-4"></div>
    </div>
    </div>
        )
    }
}


export default connect(mapStateToProps)(Home);
