import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { connect } from "react-redux";
import _ from "lodash";


class Profile extends Component {
    constructor(props) {
        super(props);
        this.state={
            data:[]
        }
        this.filteredUser={}
    }

    componentWillMount() {
        let userId = this.props.match.params.id;
        this.filteredUser = _.find(this.props.userlist, { id: userId });
        console.log(this.filteredUser);
        this.setState({
            data:this.filteredUser
        })
        console.log(userId);
    }

    render() {
        const data = parseInt(this.state.data);
        console.log(this.state);
        console.log(data);
        let viewData = "Loading";
        if(data.length > 0 ) {
            viewData = `
            <div>
              <h1>{ this.filteredUser.firstname }</h1>
              <h1>{ this.filteredUser.lastname }</h1>
              <h1>{ this.filteredUser.phone }</h1>
            </div>
            `;
        }
        return (
            { viewData }
        )
    }
}

const mapStateToProps = (state) => {
    return {
      userlist: state
    }
  }

  export default connect(mapStateToProps)(Profile);
