import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { connect } from "react-redux";
import _ from "lodash";

class Edit extends Component {
    constructor(props) {
        super(props);
        this.state={
            fname:'',
            lname:'',
            phone:''
        }
        this.filteredUser={}
        this.id= parseInt(this.props.match.params.id);
    }
    methodLogin(event){ 
        this.setState({
            fname:event.target.value
        }, (previousState) => {
            //console.log(this.state.fname);        
        })            
    }
    methodLoginlname(event){ 
        this.setState({
            lname:event.target.value
        }, (previousState) => {
            //console.log(this.state.fname);        
        })            
    }
    methodLoginphone(event){ 
        this.setState({
            phone:event.target.value
        }, (previousState) => {
            //console.log(this.state.fname);        
        })            
    }
    handleEdit(event){
        this.props.dispatch({
            type:'EDIT_USER',
            data: {
                id: 1,
                firstname: this.state.fname,
                lastname: this.state.lname,
                phone: this.state.phone
            },
          })
          //this.props.history.push('/users');
    }

    render() {
        let userId = parseInt(this.props.match.params.id);
        this.filteredUser = _.find(this.props.userlist, { id: userId });
        return (
            <div className="col-md-12 pad-side6">
              <h1>{this.props.userlist[this.props.match.params.id]}</h1>
              <form onSubmit={this.handleEdit.bind(this)}>
                {/* <input type="text" placeholder="Enter first name" defaultValue={this.filteredUser.firstname} className="form-control"  onChange={this.methodLogin.bind(this)}/>
                <input type="text" placeholder="Enter last name" defaultValue={this.filteredUser.lastname} className="form-control" onChange={this.methodLoginlname.bind(this)}/>
                <input type="text" placeholder="Enter phone" defaultValue={this.filteredUser.phone} className="form-control" onChange={this.methodLoginphone.bind(this)}/> */}
                <input type="text" placeholder="Enter first name" className="form-control"  onChange={this.methodLogin.bind(this)}/>
                <input type="text" placeholder="Enter last name"  className="form-control" onChange={this.methodLoginlname.bind(this)}/>
                <input type="text" placeholder="Enter phone"  className="form-control" onChange={this.methodLoginphone.bind(this)}/>

              <button type="submit" className="btn btn-danger">Edit</button>
              </form>
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return {
      userlist: state
    }
  }

  export default connect(mapStateToProps)(Edit);
