import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { connect } from "react-redux";
import _ from "lodash";


class Profile extends Component {
    constructor(props) {
        super(props);
        this.state={
            data:[]
        }
        this.filteredUser={}
    }

    componentWillMount() {
        let userId = parseInt(this.props.match.params.id);
        this.filteredUser = _.find(this.props.userlist, { id: userId });
        console.log(this.filteredUser);
        this.setState({
            data:this.filteredUser
        })
        console.log(userId);
    }

    render() {
        const { data } = this.state;
        console.log( data.length );
        if(data.length > 0 ) {
            return(
                <div>
                <h1>{ this.filteredUser.firstname }</h1>
                <h1>{ this.filteredUser.lastname }</h1>
                <h1>{ this.filteredUser.phone }</h1>
                </div>
            )
        } else {
            return (
                'Loading'
            )
        }
    }
}

const mapStateToProps = (state) => {
    return {
      userlist: state
    }
  }

  export default connect(mapStateToProps)(Profile);
