import _ from "lodash";

const userReducer = (state = [], action) => {
    switch(action.type) {
        case 'ADD_USER':
            return state.concat(action.data);
        case 'REMOVE_USER':
            return state.filter((item, index) => item.id !== action.data);
        // return _.remove(state, 
        //     {
        //         firstname:action.data
        //     })
        case 'EDIT_USER':
            let data=action.data;
            console.log(data);
            var index = _.findIndex(state, {id: data.id});
            console.log(state);
            // return state.splice(index, 1, data);
            return state.concat(action.data);
        default:
            return state;   
    }
}

export default userReducer;
