import React, { Component } from 'react';
import { connect } from "react-redux";

class Home extends Component {
    constructor(props){
        super(props);
        this.state={
            fname:''
        }
    }
    methodLogin(event){ 
        this.setState({
            fname:event.target.value
        }, (previousState) => {
            //console.log(this.state.fname);        
        })            
    }
    handleSubmit(event){
        event.preventDefault();
        //this.props.methodData(this.state.username);
        console.log(this.state.fname); 
        this.props.dispatch({
            type:'ADD_USER',
            data: this.state.fname
          })
    }
    render() {
        return (
            <div className="col-md-12">
            <div className="col-sm-3"></div>
            <div className="col-sm-6 sidenav">
      <h4>Create user</h4>
     <div className="input-group">
     <form onSubmit={this.handleSubmit.bind(this)}>
                <input type="text" placeholder="Enter first name" className="login-input form-control"  onChange={this.methodLogin.bind(this)}/>
                <input type="text" placeholder="Enter last name" className="login-input form-control"/>
                <input type="text" placeholder="Enter mobile no" className="login-input form-control"/>
                <button type="submit" className="btn btn-success">Submit</button>
     </form>
      </div>
    </div>
    </div>
        )
    }
}


export default connect()(Home);
