import React, { Component } from 'react';

class Home extends Component {
    constructor(props){
        super(props);
        this.state={
            username:''
        }
    }
    methodLogin(event){ 
        this.setState({
            username:event.target.value
        }, (previousState) => {
            console.log(this.state.username);        
        })            
    }
    handleSubmit(event){
        event.preventDefault();
        this.props.methodData(this.state.username);
    }
    render() {
        return (
            <div className="col-sm-3 sidenav">
      <h4>Create user</h4>
     <div className="input-group">
     <form onSubmit={this.handleSubmit.bind(this)}>
                <input type="text" placeholder="Enter first name" className="login-input form-control"  onChange={this.methodLogin.bind(this)}/>
                <input type="text" placeholder="Enter last name" className="login-input form-control"/>
                <input type="text" placeholder="Enter mobile no" className="login-input form-control"/>
                <button type="submit" className="btn btn-success">Submit</button>
     </form>
      </div>
    </div>
        )
    }
}

export default Home;
