import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { connect } from "react-redux";
import _ from "lodash";

class Delete extends Component {
    constructor(props) {
        super(props);
        this.state={
            fname:'',
            lname:'',
            phone:''
        }
        this.filteredUser={}
    }
    methodLogin(event){ 
        this.setState({
            fname:event.target.value
        }, (previousState) => {
            //console.log(this.state.fname);        
        })            
    }
    methodLoginlname(event){ 
        this.setState({
            lname:event.target.value
        }, (previousState) => {
            //console.log(this.state.fname);        
        })            
    }
    methodLoginphone(event){ 
        this.setState({
            phone:event.target.value
        }, (previousState) => {
            //console.log(this.state.fname);        
        })            
    }
    handleEdit(event, param){
        this.props.dispatch({
            type:'EDIT_USER',
            data: event
          })
          this.props.history.push('/users');
    }

    render() {
        let userId = parseInt(this.props.match.params.id);
        this.filteredUser = _.find(this.props.userlist, { id: userId });
        return (
            <div className="col-md-12 pad-side6">
              <h1>{this.props.userlist[this.props.match.params.id]}</h1>
              <input type="text" placeholder="Enter first name" value={this.filteredUser.firstname} className="form-control"  onChange={this.methodLogin.bind(this)}/>
                <input type="text" placeholder="Enter last name" value={this.filteredUser.lastname} className="form-control" onChange={this.methodLoginlname.bind(this)}/>
                <input type="text" placeholder="Enter phone" value={this.filteredUser.phone} className="form-control" onChange={this.methodLoginphone.bind(this)}/>
              <button type="submit" className="btn btn-danger" onClick = { this.handleEdit.bind(this, this.props.match.params.id) }>Edit</button>
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return {
      userlist: state
    }
  }

  export default connect(mapStateToProps)(Delete);
