import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { connect } from "react-redux";


class Users extends Component {
    constructor(props) {
        super(props);
        this.state= {
            id:''
        }
    }
    handleClick(event, param){
        // this.setState({
        //     id:event
        // })
        // alert(this.state.id);
        alert(event);
    }
    render() {
        return (
            <div className="col-sm-12">
            <table className="table">
                <thead>
                    <th>Sl no.</th>
                    <th>First name</th>
                    <th></th>
                    <th></th>
                    <th></th>
                </thead>
        {this.props.userlist.map((user, i) => {
       return <tr id={ user.id }>
           <td>{i+1}</td>
           <td>{user.firstname}</td>
           <td><Link to={`/profile/${user.id}`}>View</Link></td>
           <td><Link to={`/edit/${user.id}`}><button  //onClick = { this.handleClick.bind(this, i) }
           >Edit</button></Link></td>
           <td><Link to={`/delete/${user.id}`}><button>Remove</button></Link></td>
           </tr>
      }
     )}
      </table>
    </div>
        )
    }
}

const mapStateToProps = (state) => {
  return {
    userlist: state
  }
}

export default connect(mapStateToProps)(Users);
