const userReducer = (state = [], action) => {
    switch(action.type) {
        case 'ADD_USER':
            return state.concat(action.data);
        case 'REMOVE_USER':
        return state.filter((item, index) => item.index !== action.data);

        default:
            return state;
    }
}

export default userReducer;
